import React, {Component} from 'react';
import './App.css';




export default class App extends Component {

  constructor() {
    super();
    this.state = {sample: 'Beat ❌ichigan', value : ""
  };
  this.handleClick = this.handleClick.bind(this);

  }
handleClick = (event) => {

this.setState((state,props ,event) => ({
  sample:  event.value
}));
}

render(){
  return (
    <div className="App">
      <div className="textMain">Cross out your M's!</div>
      <input
          type="text"
          placeholder= {this.state.sample}
          onChange = {this.handleClick}
          value = {this.state.value}
        />
        <button type="button" onClick={this.handleClick}>Convert</button>
        <div className="textMain">{this.state.sample}!</div>

    </div>
  );
}
}
