  };
  this.handleClick = this.handleClick.bind(this);

  }
handleClick = () => {
this.setState = ({
  sample: 'newNext'
}), function () {
  this.props.updateItem(this.state);
}.bind(this);
}

render(){
  return (
    <div className="App">
      <div className="textMain">Cross out your M's!</div>
      <input
          type="text"
          placeholder= {this.state.sample}
        />
        <button type="button" onClick={this.handleClick}>Convert</button>
